  // })

  function handleClick() {
    // React.useEffect(() => {
  //   fetch(`https://rancid-tomatillos.herokuapp.com/api/v2/movies/${436270}`)
  //   .then(response => response.json())
  //   .then(data => setSingleMovie(data))
  // })
  }

  return (
    <section>
      <img src={singleMovie.poster_path}alt={singleMovie.title}/>
      <p>{singleMovie.title}</p>
      <p>{singleMovie.release_date}</p>
      <p>{singleMovie.overview}</p>
      <p>{singleMovie.genres}</p>
      <p>{singleMovie.budget}</p>
      <p>{singleMovie.average_rating}</p>
    </section>
  )
}
