import React from "react"
import Header from "../Header/Header";
import Top5 from "../Top5/Top5";
import AllMovies from "../AllMovies/AllMovies";
import MovieCard from "../MovieCard/MovieCard";
import './App.css';
import MovieInfo from "../MovieInfo/MovieInfo";

function App() {
  const [allMovies, setAllMovies] = React.useState([])
  const [singleMovie, setSingleMovie] = React.useState({})

  React.useEffect(() => {
    fetch('https://rancid-tomatillos.herokuapp.com/api/v2/movies')
    .then(response => response.json())
    .then(data => setAllMovies(data.movies))
    .catch(err => console.log(err))
  }, [])

  React.useEffect((event) => {
    fetch('https://rancid-tomatillos.herokuapp.com/api/v2/movies')
  })
  const allMovieCards = allMovies.map(movie => {
   return (
   <MovieCard 
   key={movie.id}
   image={movie.backdrop_path}
   title={movie.title}
   />
   ) 
  })


  return (
    <main>
      <Header />
      <Top5 />
      {<MovieInfo movieInfo={singleMovie}/> && <AllMovies allCards= {allMovieCards}/>}
    </main>
  );
}

export default App;
